import json
from typing import Any
from langflow.custom import Component
from langflow.io import (
    BoolInput,
    HandleInput,
    MessageTextInput,
    MultilineInput,
    Output,
    TabInput,
)
from langflow.schema import Data, DataFrame
from langflow.schema.message import Message


class ParserComponent(Component):
    display_name = "Parser"
    description = (
        "Format a DataFrame or Data object into text using a template. "
        "Enable 'Stringify' to convert input into a readable string instead."
    )
    icon = "braces"

    inputs = [
        TabInput(
            name="mode",
            display_name="Mode",
            options=["Parser", "Stringify"],
            value="Parser",
            info="Convert into raw string instead of using a template.",
            real_time_refresh=True,
        ),
        MultilineInput(
            name="pattern",
            display_name="Template",
            info=(
                "Use variables within curly brackets to extract values from Data or DataFrame."
                "Example: `Persona: {persona}, Message: {text}`"
            ),
            value="Text: {text}",
            dynamic=True,
            show=True,
            required=True,
        ),
        HandleInput(
            name="input_data",
            display_name="Data or DataFrame",
            input_types=["DataFrame", "Data"],
            info="Accepts either a DataFrame or a Data object.",
            required=True,
        ),
        MessageTextInput(
            name="sep",
            display_name="Separator",
            advanced=True,
            value="\n",
            info="String used to separate rows/items.",
        ),
    ]

    outputs = [
        Output(
            display_name="Parsed Text",
            name="parsed_text",
            info="Formatted text output.",
            method="parse_combined_text",
        ),
    ]

    def update_build_config(self, build_config, field_value, field_name=None):
        if field_name == "mode":
            build_config["pattern"]["show"] = self.mode == "Parser"
            build_config["pattern"]["required"] = self.mode == "Parser"
            if field_value == "Stringify":
                clean_data = BoolInput(
                    name="clean_data",
                    display_name="Clean Data",
                    info="Remove empty rows/lines in Data or DataFrame",
                    value=True,
                    advanced=True,
                )
                build_config["clean_data"] = clean_data.to_dict()
            else:
                build_config.pop("clean_data", None)
        return build_config

    def _clean_args(self):
        input_data = self.input_data
        match input_data:
            case list() if all(isinstance(item, Data) for item in input_data):
                raise ValueError("List of Data objects is not supported.")
            case DataFrame():
                return input_data, None
            case Data():
                return None, input_data
            case dict() if "data" in input_data:
                try:
                    if "columns" in input_data:
                        return DataFrame.from_dict(input_data), None
                    return None, Data(**input_data)
                except Exception as e:
                    raise ValueError(f"Invalid structured input: {e}") from e
            case _:
                raise ValueError(f"Unsupported input type: {type(input_data)}")

    def parse_combined_text(self) -> Message:
        if self.mode == "Stringify":
            return self.convert_to_string()

        df, data = self._clean_args()
        lines = []

        try:
            if df is not None:
                for _, row in df.iterrows():
                    lines.append(self.pattern.format(**row.to_dict()))
            elif data is not None:
                values = data.data
                try:
                    lines.append(self.pattern.format(**values))
                except KeyError as e:
                    msg = (
                        f"🔍 Parser pattern is missing required key: '{e.args[0]}'\n"
                        f"📦 Available keys: {list(values.keys())}\n"
                        f"🧪 Data snapshot: {json.dumps(values, indent=2)}"
                    )
                    raise KeyError(msg) from e
            else:
                lines.append("No data to parse.")
        except Exception as e:
            raise e

        combined_text = self.sep.join(lines)
        self.log(f"🧪 Data received in parser: {json.dumps(data.data, indent=2)}")
        self.status = combined_text
        return Message(text=combined_text)

    def _safe_convert(self, value: Any) -> str:
        try:
            if isinstance(value, str):
                return value
            if isinstance(value, Message):
                return value.get_text()
            if isinstance(value, Data):
                return json.dumps(value.data)
            if isinstance(value, DataFrame):
                if getattr(self, "clean_data", False):
                    value = value.dropna(how="all").replace(r"^\s*$", "", regex=True)
                return value.to_markdown(index=False)
            return str(value)
        except Exception as e:
            raise ValueError(f"Error converting data: {e}") from e

    def convert_to_string(self) -> Message:
        try:
            if isinstance(self.input_data, list):
                result = "\n".join([self._safe_convert(item) for item in self.input_data])
            else:
                result = self._safe_convert(self.input_data)
        except Exception as e:
            result = f"Error: {e}"

        self.status = result
        return Message(text=result)

from langflow.base.io.text import TextComponent
from langflow.io import MultilineInput, Output
from langflow.schema.message import Message


class TextInputComponent(TextComponent):
    display_name = "Text Input"
    description = "Get text inputs from the Playground."
    icon = "type"
    name = "TextInput"

    inputs = [
        MultilineInput(
            name="input_value",
            display_name="Text",
            info="Text to be passed as input.",
        ),
    ]
    outputs = [
        Output(display_name="Message", name="text", method="text_response"),
    ]

    def text_response(self) -> Message:
        return Message(
            text=self.input_value,
        )
